DROP DATABASE IF EXISTS film_comment_system;
-- 创建数据库
CREATE DATABASE IF NOT EXISTS film_comment_system CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci;

-- 使用该数据库
USE film_comment_system;

-- 创建电影分类表
CREATE TABLE category (
    id INT AUTO_INCREMENT PRIMARY KEY COMMENT '分类ID',
    name VARCHAR(10) COMMENT '分类名称'
);

-- 创建电影信息表
CREATE TABLE film (
    id INT AUTO_INCREMENT PRIMARY KEY COMMENT '电影ID',
    name VARCHAR(100) NOT NULL COMMENT '电影名称',
    cover VARCHAR(500) COMMENT '封面',
    descr TEXT COMMENT '描述',
    year INT COMMENT '年份',
    director VARCHAR(40) COMMENT '导演',
    actors VARCHAR(300) COMMENT '演员',
    category_id INT COMMENT '分类ID',
    country VARCHAR(10) COMMENT '国家',
    language VARCHAR(8) COMMENT '语言',
    date DATE COMMENT '上映日期',
    duration VARCHAR(10) COMMENT '片长（分钟）',
    score DECIMAL(3,1) COMMENT '评分',
    FOREIGN KEY (category_id) REFERENCES category(id)
);

-- 创建用户信息表
CREATE TABLE user (
    id INT AUTO_INCREMENT PRIMARY KEY COMMENT '用户ID',
    username VARCHAR(20) NOT NULL COMMENT '用户名/账号',
    password VARCHAR(255) NOT NULL COMMENT '密码',
    name VARCHAR(20) COMMENT '名称',
    avatar VARCHAR(120) COMMENT '头像',
    role ENUM('USER', 'ADMIN') DEFAULT 'USER' COMMENT '角色',
    salt VARCHAR(50) COMMENT '盐值',
    CONSTRAINT unique_username UNIQUE (username)
);

-- 创建管理员信息表
CREATE TABLE admin (
    id INT AUTO_INCREMENT PRIMARY KEY COMMENT '管理员ID',
    username VARCHAR(20) NOT NULL COMMENT '用户名/账号',
    password VARCHAR(255) NOT NULL COMMENT '密码',
    name VARCHAR(20) COMMENT '名称',
    avatar VARCHAR(120) COMMENT '头像',
    role ENUM('USER', 'ADMIN') DEFAULT 'ADMIN' COMMENT '角色',
    salt VARCHAR(50) COMMENT '盐值',
		CONSTRAINT unique_username UNIQUE (username)
);


-- 创建电影评论表
CREATE TABLE comment (
    `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'ID',
    `film_id` int(11) DEFAULT NULL COMMENT '电影ID',
    `score` double(11,1) DEFAULT NULL COMMENT '评分',
    `comment` longtext COLLATE utf8mb4_unicode_ci COMMENT '评论内容',
    `user_id` int(11) DEFAULT NULL COMMENT '用户ID',
    `time` varchar(30) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '评论时间',
    `type` varchar(30) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '类型',
    PRIMARY KEY (`id`),
    FOREIGN KEY (film_id) REFERENCES film(id),
    FOREIGN KEY (user_id) REFERENCES user(id)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

-- 创建公告表
CREATE TABLE notice (
    id INT AUTO_INCREMENT PRIMARY KEY COMMENT '公告ID',
    title VARCHAR(100) COMMENT '标题',
    content TEXT COMMENT '内容',
    time DATETIME COMMENT '发布时间'
);
CREATE INDEX idx_film_category_id ON film(category_id);
CREATE INDEX idx_comment_film_id ON comment(film_id);
CREATE INDEX idx_comment_user_id ON comment(user_id);


